-- ----------------------------
-- Table structure for t_regions
-- ----------------------------
DROP TABLE IF EXISTS `t_regions`;
CREATE TABLE `t_regions` (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT,
  `UUID` varchar(255) DEFAULT NULL COMMENT '区域编号',
  `FName` varchar(255) DEFAULT NULL COMMENT '区域名称',
  `FParentRegionsId` bigint(20) DEFAULT '0' COMMENT '上级区域',
  `FCnName` varchar(255) DEFAULT NULL COMMENT '区域名称拼音缩写',
  `FExpand` varchar(2048) DEFAULT NULL COMMENT '预留',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

