-- ----------------------------
-- Table structure for t_task_config
-- ----------------------------
DROP TABLE IF EXISTS `t_task_config`;
CREATE TABLE `t_task_config` (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT,
  `FName` varchar(255) DEFAULT NULL,
  `FUrl` varchar(1024) DEFAULT NULL,
  `FCode` varchar(255) DEFAULT NULL,
  `FCycle` varchar(255) DEFAULT NULL COMMENT '周期 表达式',
  `FMenuUrl` varchar(255) DEFAULT NULL,
  `FStatus` int(1) DEFAULT '1' COMMENT '1：正常；2：暂停；',
  `FIsTask` int(1) DEFAULT '1' COMMENT '是否为定时任务,1：是，0：否',
  `FExpand` text,
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

